use std::str::FromStr;

use criterion::{black_box, Criterion, BenchmarkId};

use bigdecimal::BigDecimal;
use fastnum::decimal::UnsignedDecimal;

macro_rules! macro_impl {
    ($group: ident, $bits: literal, $str: literal) => {{
            const STR: &'static str = $str;
            
            let size = STR.len();
            
            $group.bench_with_input(BenchmarkId::new("f64", size), &size, |b, _| {
                b.iter(|| black_box(f64::from_str(STR).unwrap()))
            });
        
            $group.bench_with_input(BenchmarkId::new("fastnum", size), &size, |b, _| {
                b.iter(|| black_box(UnsignedDecimal::<{$bits / 64}>::from_str(STR).unwrap()))
            });
        
            $group.bench_with_input(BenchmarkId::new("bigdecimal", size), &size, |b, _| {
                b.iter(|| black_box(BigDecimal::from_str(STR).unwrap()))
            });
        }
    };
}

pub fn from_str(c: &mut Criterion) {
    let mut group = c.benchmark_group("from_str");

    macro_impl!(group, 128, "1.0");
    macro_impl!(group, 128, "1.23456789");
    macro_impl!(group, 128, "3.40282366920938463463374607431768211455");
    macro_impl!(group, 256, "1.15792089237316195423570985008687907853269984665640564039457584007913129639935");
    macro_impl!(group, 512, "1.3407807929942597099574024998205846127479365820592393377723561443721764030073546976801874298166903427690031858186486050853753882811946569946433649006084095");
    // macro_impl!(group, 1024, "1.");
    // macro_impl!(group, 2048, "3.");
    // macro_impl!(group, 4096, "1.");
    // macro_impl!(group, 8192, "1.");
    
    group.finish();
}

